INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('Eagle Eyes Mini Pdr Light','$200.00','This LED light has a great natural fog generated by the shadow between the LED light stripes. Made for professionals! It is used as a back-up by those who use battery powered light and as a starter light for those beginning a career in professional Paintless Dent Repair. Because this light has the same LED lens assembly as our more expensive professional battery powered it is the obvious choice if you are wanting a corded Mini PDR Light. 12 volt plug Three light strips (cool-warm-cool) 12" Loc-Line 4.5" Woods Power Grip (suction cup) Dimmable, on/off switch Light, impact resistant construction impact resistant lens (unbreakable) 16" Lens','Product Code: eemc3c',"'http://pdrfinessetools.com/image/cache/catalog/jason_added/lights/Eagle-Eyes-2-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/jason_added/lights/Eagle-Eyes-1-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/jason_added/lights/Eagle-Eyes-5-150x150.jpg'",'http://pdrfinessetools.com/eemc3c','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 12:39:35');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#152- 41" Long 1/2" Diameter Slow Bend Shaved With Sharp Tip','$110.00','41" 1/2 Slow Bend Shaved with Sharp Tip for Pushing or Twisting. A great tool with a lot of versatility to gather and shrink deep dents. For finishing tight long creases. It also works as a twist tool in tight areas, under braces, and around wheel houses. A great tool design with performance.','Product Code: 152',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/152-250x250.JPG', 'http://pdrfinessetools.com/image/cache/catalog/new%20tools/152%20(3)-150x150.JPG', 'http://pdrfinessetools.com/image/cache/catalog/new%20tools/152%20(4)-150x150.JPG'",'http://pdrfinessetools.com/By%20Design/42quot-12quot-diameter-slow-bend-shaved-with-sharp-tip','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 12:39:37');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#222  -Changeable Tip Flat Bar','$200.00','22" Long Flat Bar with a Changeable Tip Attachment.  The bar is 1 1/2" wide and 3/16" thick, hardened stainless steel flat bar.  Comes with one 3/8" soft tip with a rubber cap and one 3/4" rounded custom metal tip.  Tempered for stiffness.  This is an old school tool that works better than most any flat bar made today, accesses down through window openings, between the dust shield and outer fender skin, through headlight and taillight openings and many other places that you can discover for this tool.  Super hard drive for the nasty dents.','Product Code: 222',"'http://pdrfinessetools.com/image/cache/catalog/222-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/IMG_7466-150x150.JPG', 'http://pdrfinessetools.com/image/cache/catalog/IMG_7468-150x150.JPG'",'http://pdrfinessetools.com/By%20Design/flat%20bars?product_id=458','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 12:39:40');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('Walk Platform','$275.00','The Walk Platform is designed to use sitting or standing.  The top diamond plate platform is 3 ft long x 18 in wide.  The legs are 15 inches long and can extend to 24 inches.  It has an optional glue tray that is available for an aditional $150.00.  This stand is designed to be extremely stable to give a sense of security to the user.  Recomended weight not to exceed 400 lbs.  However, we tested it for 800 lbs.   This tool tray is designed to be light weight, sturdy and very portable for transport.','Product Code: bmwp',"'http://pdrfinessetools.com/image/cache/catalog/tools%20fall%202012/big%20boy%202-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/tools%20fall%202012/big%20boy%203-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/tools%20fall%202012/big%20boy-150x150.jpg'",'http://pdrfinessetools.com/By%20Design/aluminum%20products/workbench','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 12:39:43');
