INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#09-  72" Long 3/4" Flip Tip Roof Rod','$250.00','72" long Interchangable flip tip roof rod.  Made out of the 3/4" rod. It is very stiff and great for reaching across SUV or mini van roofs. With the flip of your wrist you will have two tools at your disposal and with the interchangable tips you will have any two tools you want. Great roof rod, comes with 2 tips, and excepts 5/16\' 18 thread count (Industry standard) tips.','Product Code: 9',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/09%20whole-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/new%20tools/9-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/new%20tools/09%20tip-150x150.jpg'",'http://pdrfinessetools.com/By%20Design/Roof%20Tools/9','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 12:39:48');
