INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#185Bb -15" Long 1/4" Diameter Double Bend Tapered Hook Tool','$60.00','This access/hand tool is fitted with a tapered hook after two bends for working small dents and tight areas, especially through the headlight and taillight screw holes.  Great push and twist tool.','Product Code: 185bb',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/IMG_7371-250x250.JPG', 'http://pdrfinessetools.com/image/cache/catalog/new%20tools/IMG_7373-150x150.JPG'",'http://pdrfinessetools.com/By%20Design/Double%20Bend/185bb','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 12:39:52');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('Trio Of Tri-Picks','$310.00','The Tri-Pick Trio is a group of our most popular pick tools, the set includes: 183 a, 48 a and 44 w.  To view these tools individually go to the \'related products\' tab.  This set is valued at $340.00.','Product Code: triset',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/tripick%20trio-250x250.JPG'",'http://pdrfinessetools.com/By%20Design/Triple%20Bend/triset','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 12:39:55');
