INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#87P-  12" Long 3/16" Diameter 15°  Raised Flag Set','$79.00','Set of 12" long 3/16" diameter flags raised 1" off the handle for better access and the extra drive you gain with the bend adding 1" to the overall length of the handle. A great tool, and one of our better sellers. 45 degree shaved flag. The raise in the shaft also prevents the scraping of your hands on the sharp edges of the sheet metal. Set handles are color coded to distinguish right from left.','Product Code: 087p',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/87-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/87%20TIP-500x500-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/87%20END-500x500-150x150.jpg'",'http://pdrfinessetools.com/By%20Design/Flag%20Sets/14-inch-15-raised-flag-set','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 12:39:59');
