INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#68S- 35" Long 1/4" Diameter Short Flag','$95.00','35" Long 1/4" diameter shaved and rounded thin back 24" with a 1" flag. Sharpened on the leading edge for cutting glue.  Also, with thin rounded sides for better access on hard to reach dents.  This tool has excellent drive and is designed to be used through clip holes on aluminum hood pads.  Its a fantastic aluminum hood tool with hard drive that will work in many places where tight access is an issue.  Very thin and flexible.  Can also be used like a whale tail when needed.','Product Code: 068s',"'http://pdrfinessetools.com/image/cache/catalog/68s-250x250.JPG', 'http://pdrfinessetools.com/image/cache/catalog/68s%20(2)-150x150.JPG'",'http://pdrfinessetools.com/By%20Design/shavedaccess1/Long%20Rods/68s','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 12:41:19');
