INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#36R-  24" Long 5/16" Diameter Door Tool With A Reverse Adjustable Handle','$160.00','This door tool is a 24" long 5/16" diameter slow bend shaved 4" long 82° bend flag, with a straight 6" long reverse adjustable handle.  The difference between this tool and the regular door tools (with an adjustable handle) is that this tool features the adjustable handle gear reversed.  Reversing the gear transforms this tool into a brace tool that will not slip out of gear because the tech has to push down to make the handle change positions.','Product Code: 036R',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/36r-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/36%20END-500x500-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/36%20TIP-500x500-150x150.jpg'",'http://pdrfinessetools.com/By%20Design/Door%20Tools?product_id=440','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 12:42:22');
