INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('Leverage Bar With Accessories And Brackets','$250.00','This leverage bar for SUV and Minivan roofs extends from 37 inches to 63 inches.  Fits the smallest foreign SUVs up to the largest van roofs.  Milled grooves cover the bar to keep your tools steady and on the dent.  Light and sturdy, made from high quality aircraft aluminum.  Also included with this product is two hood brackets, two T-shaped brackets, two chains and two tapered S hooks.  (Hoodstand not included).  Below are pictures of the brackets and the leverage bar on the hoodstand.','Product Code: lbhs',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/leverage%20bar-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/new%20tools/brackets2-150x150.jpg'",'http://pdrfinessetools.com/By%20Design/aluminum%20products/lba','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 12:52:39');
