INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#55- 35" Long 5/16" Diameter Double Bend','$75.00','35" double bend 5/16" diameter shaved tool that was designed to reach almost anywhere inside a door panel. This tool is a favorite of Terry Siegle from the KC area and will work well for anyone. It is strictly a twist tool but has the drive to get the job done. Works especially well inside of doors where access is everything. It has a 3 and 1/2" kick.','Product Code: 55',"'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/55-500x500-250x250.jpg'",'http://pdrfinessetools.com/by%20diamtre/516%20inch%20/PDR-Tools-36-inch-double-bend','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:17:03');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#41L-   24" Long 5/16" Diameter 70°  Shaved Sharp Tip Door Tool With An Adjustable Handle','$150.00','Sharp Door Tool  24" long 5/16" diameter slow sweeping 70°  shaved sharp tip. This tool was designed for the EURO and EXOTIC cars but works well on any make of vehicle. It comes with a 6" straight adjustable handle for extra control and drive. The handle is always in the perfect position for comfort and ease while pushing. An excellent finishing tool with a sharp tip.','Product Code: 041L',"'http://pdrfinessetools.com/image/cache/catalog/tools%20fall%202012/41%20L%20(4)-250x250.JPG', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/41%20TIP-500x500-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/41%20END-500x500-150x150.jpg'",'http://pdrfinessetools.com/by%20diamtre/516%20inch%20/41l','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:17:05');
