INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#37-   20" Long 5/16" Diameter Double Bend Shaved J With A Reverse Adjustable Handle','$160.00','Upper Brace Tool  20" long double bend shaved J upper brace hook tool with a reverse adjustable straight handle that makes it a right and left tool. This is the only upper brace tool that will reach around the door mirror clear up to the front edge of the door. It is shaved thin for access while retaining it hard driving capabilities. From the bottom of the hook to the tip is 4". Designed by techs for techs. This tool is fast becoming one of our best sellers because of its versatility. It will reach those impossible dents like through stake pockets, near tops of bedsides and many other places that a straight shaft tool just can not reach. It is a fantastic, versatile tool that will pay for itself many times over. (Not just for under braces).','Product Code: 37',"'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/37-500x500-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/37%20tip-500x500-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/37%20end-500x500-150x150.jpg'",'http://pdrfinessetools.com/by%20diamtre/516%20inch%20/21-inch-double-bend-shaved-516','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:17:08');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#36Sr-   16" Long 5/16" Diameter Door Tool With 4" Of Kick With A Reverse Adjustable Handle','$150.00','This is shorter version of our now famous door tool. It has the same 4 inch kick, but it is 8 inches shorter. Perfect for working the top portion of a door under the top brace.  The unique part of this tool is that it has the adjustable handle gear upsidedown, so the gear can not slip out when pulling up on braces.','Product Code: 036R-S',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/36%20SR-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/new%20tools/36%20SR%20END-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/new%20tools/36%20SR%20TIP-150x150.jpg'",'http://pdrfinessetools.com/by%20diamtre/516%20inch%20?product_id=439','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:17:10');
