INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#249,   28", 1/2" Diameter, Door Tool, Slow Bend, And A Shaved Rounded Tip','$90.00','#249   28" long  1/2" Diameter  Door Tool  Slow Bend Welded T-handle 2  3/4 in. kick  This is a very heavy duty door tool. Well suited for all types of hard pushing especially for crease or body line areas. It has a long flat shaved taper on the top and bottom and none on the right or left side. Similar to a flat bar on the pushing end. It has a slow Bend making it easy to work into tight areas with a kick of 2 3/4 in. Tool #249 PDF','Product Code: 249',"'http://pdrfinessetools.com/image/cache/catalog/jason_added/num-249/249-580px-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/jason_added/num-249/side-249-500x500-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/jason_added/num-249/tip-detail-249-500x500-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/jason_added/num-249/top-249-500x500-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/jason_added/num-249/249-tip-580px-150x150.jpg'",'http://pdrfinessetools.com/index.php?route=product/product&product_id=573','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:22:25');
