INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('Large Plastic Glue Tray','$150.00','The "Large Plastic Glue Tray" is made of heavy duty reinforced plastic. It is center mounted on the included aluminum mounting arm. This kit also includes the extra height extension.  Large Plastic Glue Tray dimensions: 19, 3/8" X 14, 1/2"','Product Code: LPGT',"'http://pdrfinessetools.com/image/cache/catalog/jason_added/fold-n-go_tool_cart/Untitled%20presentation%20(1)-250x250.jpg'",'http://pdrfinessetools.com/LPGT','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:22:33');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('Leverage Bar With Accessories And Brackets','$250.00','This leverage bar for SUV and Minivan roofs extends from 37 inches to 63 inches.  Fits the smallest foreign SUVs up to the largest van roofs.  Milled grooves cover the bar to keep your tools steady and on the dent.  Light and sturdy, made from high quality aircraft aluminum.  Also included with this product is two hood brackets, two T-shaped brackets, two chains and two tapered S hooks.  (Hoodstand not included).  Below are pictures of the brackets and the leverage bar on the hoodstand.','Product Code: lbhs',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/leverage%20bar-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/new%20tools/brackets2-150x150.jpg'",'http://pdrfinessetools.com/lba','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:22:35');
