INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('Glue Traxx Pdr Glue Pulling Kit','$280.00','Part #gtpk  Glue Traxx PDR Glue Pulling Kit comes with everything that you need to start removing larger dents and creases in vehicles. This kit comes with a slide hammer, high-temperature glue gun, knock down, release agent bottle, glue sticks and a large assortment of PDR glue tabs.','Product Code:  Glue Traxx P',"'http://pdrfinessetools.com/image/cache/catalog/jason_added/Glue-Pulling/Glue-Traxx1-250x250.jpg'",'http://pdrfinessetools.com/Other%20Stuff/glue-pulling?product_id=563','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:22:45');
