INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('12V Glue Gun','$25.00','12v glue gun at 40 watts is a great glue gun for PDR Techs that need more mobility than a 110 plug in glue gun.  This glue gun has a 10 foot cord.','Product Code: 12v glue gun',"'http://pdrfinessetools.com/image/cache/catalog/tools%20fall%202012/HG-110-12V-LG(1)-250x250.jpg'",'http://pdrfinessetools.com/Other%20Stuff/glue-pulling/12v','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:22:51');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#990  Slide Hammer And D-Man T Glue Puller Set','$160.00','The 18" Slide Hammer and the 7\' T handle glue puller purchased together for a savings of $20. These tools are large dent dream tools, a must have set for glue pulling .       #103  7" long T handle glue puller. It has a groove cut around the tip where the glue tab inserts, with an O\'ring so if the tab snaps loose it doesn\'t roll under the car or fly across the room. The O-ring also keeps the tool from scratching the vehicle if laid on it. The DManT allows you to pull pressure on large or small dents while working the crown to relieve the pressure on the dent. This tool was designed by a body man that is now a full time PDR tech. It is a fantastic tool to work large dents. The T handle has holes in it to hook it up to a rachet strap for hard glue pulling with both hands free to work the crowns or help the dent out by other methods. It is a large dent dream tool.       #102  Here is our slide hammer. It is made of stainless steel and is18 inches long. It weighs 2.3 pounds and it can be used like the DMan T glue puller.','Product Code: 990',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/103,102-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/102a-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/102c-150x150.jpg'",'http://pdrfinessetools.com/Other%20Stuff/glue-pulling/990','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:22:52');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#103-  Dman T Glue Puller','$55.00','7" long T handle glue puller. It has a groove cut around the tip where the glue tab inserts, with an O\'ring so if the tab snaps loose it doesn\'t roll under the car or fly across the room. The O-ring also keeps the tool from scratching the vehicle if laid on it. The DManT allows you to pull pressure on large or small dents while working the crown to relieve the pressure on the dent. This tool was designed by a body man that is now a full time PDR tech. It is a fantastic tool to work large dents. The T handle has holes in it to hook it up to a rachet strap for hard glue pulling with both hands free to work the crowns or help the dent out by other methods. It is a large dent dream tool. Sold as a set with 18" Slide Hammer for $161, see related products for more information on the 102 & 103 set.','Product Code: 103',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/103-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/new%20tools/103%20(2)-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/103b-150x150.jpg'",'http://pdrfinessetools.com/Other%20Stuff/glue-pulling/PDR-Dent-Tool-Glue-Puller','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:22:55');
