INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#Xpg-10 Plain Jane Xtreme Purple Glue','$15.00','Plain Jane Xtreme Purple - The Plain Jane Xtreme Purple glue is a high strength all weather dent removal glue. The purple PDR glue is working best in warm to cool weather and is best when used with a high temperature glue gun. This dent repair glue gives you a great strong glue pull and is removed easily with 91% isopropyl alcohol.','Product Code:  #Xpg-10 Plai',"'http://pdrfinessetools.com/image/cache/catalog/Glue_Products/PBG-10-250x250.jpeg'",'http://pdrfinessetools.com/XPG-10','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:23:00');
