INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('Changeable Tip Blending Hammer Set','$200.00','This blending hammer set has the abillity to except all of the same tips as our other interchangable tipped tools. These blending hammers have 14 inch and 20 inch handles and it comes with 2 tips. Very nice hammer set. The tips and the block are just to show options.  This set is valued at $210.00.','Product Code: 104IT, 104IT-L',"'http://pdrfinessetools.com/image/cache/catalog/tools%20fall%202012/104%20it%20set%20(3)-250x250.JPG', 'http://pdrfinessetools.com/image/cache/catalog/tools%20fall%202012/104%20it%20set%20(11)-150x150.JPG'",'http://pdrfinessetools.com/Other%20Stuff/hammers-knockdowns?product_id=473','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:25:26');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#215- Knuckle Bar','$65.00','The knuckle bar is made out of solid stainless steel, very heavy duty design but easy to work.  Great for fixing hoods, roofs, and deck lids.  Basically its a good tool to use when access is not a problem and you need control of the dent, very hands on tool.  Comes with tips on both sides, one fixed round soft tip and a sharp changeable tip on other side.','Product Code: 215',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/215-500x500-250x250.JPG', 'http://pdrfinessetools.com/image/cache/catalog/new%20tools/IMG_7595-150x150.JPG', 'http://pdrfinessetools.com/image/cache/catalog/new%20tools/IMG_7601-150x150.JPG'",'http://pdrfinessetools.com/Other%20Stuff/hammers-knockdowns/knucklebar','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:25:29');
