INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('Whale Tail Tip','$25.00','1 1/8" rounded face whale tail tip that is fitted to go on any changeable tip rod.  This tool can be used to rebuild bodylines on fenders, roofs and anywhere the tip can fit.  It also comes with a nut so the tip can be tightened on to the changeable tip rod.','Product Code: WTTIP',"'http://pdrfinessetools.com/image/cache/catalog/tools%20fall%202012/2012-11-09_12-29-41_249%20(1)-250x250.jpg'",'http://pdrfinessetools.com/wttip','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:25:54');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('Walk Platform','$275.00','The Walk Platform is designed to use sitting or standing.  The top diamond plate platform is 3 ft long x 18 in wide.  The legs are 15 inches long and can extend to 24 inches.  It has an optional glue tray that is available for an aditional $150.00.  This stand is designed to be extremely stable to give a sense of security to the user.  Recomended weight not to exceed 400 lbs.  However, we tested it for 800 lbs.   This tool tray is designed to be light weight, sturdy and very portable for transport.','Product Code: bmwp',"'http://pdrfinessetools.com/image/cache/catalog/tools%20fall%202012/big%20boy%202-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/tools%20fall%202012/big%20boy%203-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/tools%20fall%202012/big%20boy-150x150.jpg'",'http://pdrfinessetools.com/workbench','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:25:56');
