INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#143A-  23" Long 7/16" Diameter Fender Tool With Adjustable Handlle','$175.00','23" Long 7/16" diameter slow bend tapered down to a small bullet round tip. This tool was designed to access all vehicle front fenders with plastic fender liners. It accesses between the liner and the fender skin and covers most of the fender, including the edge of the wheel opening. The tool has a very unique design in the bends that allows the use of the tire for leveraging off of while working the dent. (A great fender tool, and a must on every box.)  Includes an adjustable T handle.  Also available in a welded T handle,  #143W','Product Code: 143a',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/143-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/143%20tip-500x500-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/143%20end-500x500-150x150.jpg'",'http://pdrfinessetools.com/By%20Design/25-inch-triple-bend-bullet-tip','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:37:10');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#143W-  23" Long 7/16" Diameter Fender Tool','$125.00','7/16" 23" long triple bend fender tool.  Designed to allow you to use the tire for leverage.  Great for working the character lines around the tire opening, will also work over 85% of the fender.  Welded T handle.','Product Code: 143w',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/143w-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/143%20tip-500x500-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/143%20end-500x500-150x150.jpg'",'http://pdrfinessetools.com/By%20Design/143W','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:37:12');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#05-   48" Long 9/16" Diameter Triple Bend','$125.00','Buddy Tool  46" 9/16" diameter triple bend rod. This rod is designed after the original buddy tool design, except it is much larger in size and performance. It applies well on dents from the large categories to hail dents. It is very versatile and a favorite big rod for many techs.','Product Code: 5',"'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/05-500x500-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/05%20end-500x500-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/05%20tip-500x500-150x150.jpg'",'http://pdrfinessetools.com/By%20Design/5','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:37:14');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#53W-  35" Long 1/2" Diameter Double Bend Changeable Tipped Tool','$150.00','35" Long 1/2" Diameter Double Bend Interchangeable Tipped Tool.','Product Code: 053w',"'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/53-500x500-250x250.jpg', 'http://pdrfinessetools.com/By%20Design/53', 'http://pdrfinessetools.com/By%20Design/53'",'http://pdrfinessetools.com/By%20Design/53','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:37:17');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#44A- 25" Long 3/8" Diameter Buddy Tool With An Adjustable Handle','$150.00','Buddy Tool, Witch\'s Claw or Tri-pick  25 inch long, 3/8" dia. triple bend, 3 1/2 inch kick, sharp tip, adjustable T handle, buddy tool.','Product Code: 044a',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/44%20(2)-250x250.JPG', 'http://pdrfinessetools.com/image/cache/catalog/new%20tools/44%20(3)-150x150.JPG', 'http://pdrfinessetools.com/image/cache/catalog/new%20tools/44%20(4)-150x150.JPG'",'http://pdrfinessetools.com/By%20Design/25-inch-triple-bend-shaved-38','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:37:18');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#104-  14" Long 1/2" Diameter Head Blending Hammer','$70.00','14" long 1/2" diameter head hammer with two different heads. One side has a 1/2" face and the other side has a smaller face. All stainless steel, polished to a mirror finish. This hammer is more commonly used to blend a dent into the surrounding area, much like an auto re-finisher blends paint to make the painted area invisible.','Product Code: 104',"'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/104-500x500-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/104%20END-500x500-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/104%20TIP-500x500-150x150.jpg'",'http://pdrfinessetools.com/By%20Design/15-inch-blending-hammer-','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:37:22');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('Flat Flag Set Of 3 - 205, 206, 207','$125.00','These flags are all made out of flat stock with hard drive. Tapered at the end for easy access.  They work both right and left.  Can work as combination flags or double as whale tail.  Sharpend on the leading edge for cutting glue.  The Flag Set includes the following tools: 205 , 206 and 207 .  This set is valued at $140.00.','Product Code: 205,206,207',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/205,6,7-250x250.jpg'",'http://pdrfinessetools.com/By%20Design/flat_flag_set','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:37:25');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#990  Slide Hammer And D-Man T Glue Puller Set','$160.00','The 18" Slide Hammer and the 7\' T handle glue puller purchased together for a savings of $20. These tools are large dent dream tools, a must have set for glue pulling .       #103  7" long T handle glue puller. It has a groove cut around the tip where the glue tab inserts, with an O\'ring so if the tab snaps loose it doesn\'t roll under the car or fly across the room. The O-ring also keeps the tool from scratching the vehicle if laid on it. The DManT allows you to pull pressure on large or small dents while working the crown to relieve the pressure on the dent. This tool was designed by a body man that is now a full time PDR tech. It is a fantastic tool to work large dents. The T handle has holes in it to hook it up to a rachet strap for hard glue pulling with both hands free to work the crowns or help the dent out by other methods. It is a large dent dream tool.       #102  Here is our slide hammer. It is made of stainless steel and is18 inches long. It weighs 2.3 pounds and it can be used like the DMan T glue puller.','Product Code: 990',"'http://pdrfinessetools.com/image/cache/catalog/new%20tools/103,102-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/102a-150x150.jpg', 'http://pdrfinessetools.com/image/cache/catalog/102c-150x150.jpg'",'http://pdrfinessetools.com/By%20Design/990','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:37:27');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#99-  Round Tip Hand Ball Pusher','$20.00','1 and 1/2 in. round ball palm push tool.  It is very fast, and efficient for pushing dents out in the open around edges of panels.','Product Code: 99',"'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/99-500x500-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/99%20tip-500x500-150x150.jpg'",'http://pdrfinessetools.com/By%20Design/PDR-Tool-Round-Tip-Hand-Ball-Pusher','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:37:28');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#98-  Medium Tip Hand Ball Pusher','$20.00','This little hand tool has an 1 1/2 round ball handle that fit your hand perfectly.  It is designed to push out dents that are up close, out in the open on the edges of panels.  It allows you to work very fast, and efficiently. (built in 3 different designed tips-  sharp, medium, and round)','Product Code: 98',"'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/98-500x500-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/98%20tip-500x500-150x150.jpg'",'http://pdrfinessetools.com/By%20Design/PDR-Tool-Medium-Tip-Hand-Ball-Pusher','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:37:30');
INSERT INTO `all_finesse_tools` (`product_title`,`price`,`description`,`product_code`,`image_urls`,`url`,`project`,`spider`,`server`,`date`) VALUES ('#97-  Sharp Tip Hand Ball Pusher','$20.00','1 and 1/2 inch round ball hand push tool.  This little hand push tool fits your hand perfectly, and is a very fast easy method of finishing dents in the open or around the edges of panels.','Product Code: 97',"'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/97-500x500-250x250.jpg', 'http://pdrfinessetools.com/image/cache/catalog/trash%20tools/jan%202012%20images/new%20images/97%20tip-500x500-150x150.jpg'",'http://pdrfinessetools.com/By%20Design/PDR-Tools-Sharp-Tip-Hand-Pusher','properties','manual2','DESKTOP-U4HKUI7','2018-07-14 13:37:34');
